<?php

namespace Bookshop\BookshopBundle\Entity\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * ProductRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ProductRepository extends EntityRepository
{

    public function getLatestProducts($limit = null)
    {
        $qb = $this->createQueryBuilder('p')
                ->select('p')
                ->addOrderBy('p.id', 'DESC');

        if (false === is_null($limit))
            $qb->setMaxResults($limit);

        return $qb->getQuery()
                        ->getResult();
    }

    public function getFilteredProducts($cid = null, $price = null, $stock = null, $sortBy = null, $direction = null, $search = null)
    {
        $qb = $this->createQueryBuilder('p')
                ->select('p');
        if (!is_null($cid)) {
            $qb->where('p.category=' . $cid);
        } else {
            $qb->where('1=1');
        }

        if (!is_null($price)) {
            switch ($price) {
                case 1:
                    $qb->andWhere("p.price <50");
                    break;
                case 2:
                    $qb->andWhere("p.price >=50 and p.price<100");
                    break;
                case 3:
                    $qb->andWhere("p.price >=100");
                    break;
            }
        }

        if (!is_null($search)) {
            $qb->andwhere('p.title LIKE ?1')->setParameter(1, '%' . $search . '%');
        }

        if (!is_null($stock)) {
            switch ($stock) {
                case 0:
                    $qb->andWhere("p.stock =0");
                    break;
                case 1:
                    $qb->andWhere("p.stock >0");
                    break;
            }
        }

        if (!is_null($sortBy)) {
            $qb->addOrderBy("p." . $sortBy, $direction);
        }

        return $qb->getQuery()
                        ->getResult();
    }

}
